transactions.link_views_collections:
import MetadataViews from 0xMetadataViews

transaction(collectionPublicPath: String, collectionStoragePath: String) {
    prepare(acct: AuthAccount) {
        pre {
            acct.borrow<&AnyResource>(from: StoragePath(identifier: collectionStoragePath)!) != nil: "No Collection On Provided Storage Path."
        }
        let publicPath = PublicPath(identifier: collectionPublicPath)!
        let storagePath = StoragePath(identifier: collectionStoragePath)!
        if acct.getCapability<&AnyResource{MetadataViews.ResolverCollection}>(publicPath).borrow() == nil {
            acct.link<&AnyResource{MetadataViews.ResolverCollection}>(publicPath, target: storagePath)
        }
    }

    execute {
    }
}

